firebase.initializeApp({
  apiKey: "AIzaSyAyTPzyoOF08DKgBsJmct0M544F8dEx6K4",
  authDomain: "pablozon-77426.firebaseapp.com",
  projectId: "pablozon-77426",
  storageBucket: "pablozon-77426.appspot.com",
  messagingSenderId: "510211976483",
  appId: "1:510211976483:web:6be1bb526bc48ac3023096",
  measurementId: "G-BQK0RLTQNE"
})
const firestore = firebase.firestore()

function Contact(e){
  function update(){
    setSender(sender)
    setEmail(email)
    setSender(content)
  }
  const messagesRef = firestore.collection("messages")
  const query = messagesRef.orderBy("createdAt").limit(100)
  const[sender, setSender] = useState("")
  const[email, setEmail] = useState("")
  const[content, setContent] = useState("")
  return (
    <div className='contact' id='contact'>
      <div className='titleAbout'>
        <p>Contact me</p>
        <div className="line"></div>
      </div>
      <div className='message'>
        <div className='input1'>
          <p>Your Name</p>
          <input placeholder='Type it!' type="text" onChange={event => setSender(event.target.value)}/> 
        </div>
        <div className='input2'>
          <p>Your e-mail</p>
          <input placeholder='Type it!' type="text" onChange={event => setEmail(event.target.value)}/>
        </div>
        <div className='input3'>
          <p>Text me something</p>
          <input placeholder='Type it!' type="text" onChange={event => setContent(event.target.value)}/>
        </div>
        <input type="submit" className="submit"/>
      </div>
    </div>
  )
}

export default Contact
